import React from 'react';
import { AppDispatch, useAppDispatch } from './modules/redux/store';
import { dismissAll, dismissDrawer, presentDrawer } from './components/drawer/DrawerAction';
import { DrawerPosition, DrawerSize } from './components/drawer/types';
import MainContainer from './MainContainer';
import { styled } from 'styled-components';
import FlightInformation from './components/FlightInformation';
import FlightListings from './components/FlightListings';

const DivWrap = styled.div`
  width: 100%;
  height: 100%;
  /* background-color: #000; */
`;

const MyComponent = () => {
  const dispatch = useAppDispatch();
  // Use the drawer state from Redux

  return (
    <DivWrap>
      {/* Display a button to open a new drawer */}
      <button
        onClick={() =>
          dispatch(
            presentDrawer({
              component: FlightListings,
              position: DrawerPosition.BOTTOM,
              data: {
                flightNumber: '1234',
                flightDate: '2021-09-01',
                flightTime: '12:00',
              },
              event: {
                onClose: () => {
                  dispatch(dismissAll());
                },
              },
            }),
          )
        }
      >
        Open New Drawer
      </button>
    </DivWrap>
  );
};

export default MyComponent;
