import { ToastType } from './types';

export const CLEAR_TOASTS = 'CLEAR_TOASTS';
export const ALERT = 'ALERT';
export const SUCCESS = 'SUCCESS';
export const ERROR = 'ERROR';
export const SHOW = 'SHOW';
export const HIDE = 'HIDE';
export const HIDE_ALL = 'HIDE_ALL';

export function clearToasts() {
  return {
    type: CLEAR_TOASTS,
  };
}
export function show(payload: {
  type: ToastType;
  message: any;
  duration?: number;
  clear?: boolean;
  useCloseButton?: boolean;
}) {
  return {
    type: payload.type,
    payload,
  };
}

export function alert(payload: { message: any }) {
  show({ type: ToastType.ALERT, message: payload.message });
}

export function success(payload: { message: any }) {
  show({ type: ToastType.SUCCESS, message: payload.message });
}

export function showError(payload: { message: any }) {
  show({ type: ToastType.ERROR, message: payload.message });
}

export function hide(id: any) {
  return {
    type: HIDE,
    payload: { id },
  };
}

export function hideAll() {
  return {
    type: HIDE_ALL,
  };
}
