import React from 'react';
import { AppDispatch } from './redux/store';
import { useDispatch, useSelector } from 'react-redux';
import { dismissAll, dismissDrawer, presentDrawer } from './components/drawer/DrawerAction';
import { DrawerPosition } from './components/drawer/types';
import MainContainer from './MainContainer';
import DrawerItem from './components/drawer/components/DrawerItem';
import Drawer from './components/drawer/components/Drawer';
const MyComponent = () => {
  // Use the drawer state from Redux
  // const drawerState = useSelector((state) => state.drawer);

  const drawerState = useSelector((state: any) => state.drawer);

  // Dispatch function to dispatch actions

  const dispatch = useDispatch<AppDispatch>();

  // Open a new drawer item
  const handleOpenDrawer = (drawerPayload: any) => {
    dispatch(presentDrawer(drawerPayload));
    console.log(drawerState, ' ###');
  };

  // Close a specific drawer item
  const handleCloseDrawer = (drawerId: any) => {
    dispatch(dismissDrawer(drawerId));
  };

  // Close all drawers
  const handleCloseAllDrawers = () => {
    dispatch(dismissAll());
  };

  return (
    <div>
      {/* Display a button to open a new drawer */}
      <button
        onClick={() =>
          handleOpenDrawer({
            component: 'MainContainer',
            position: DrawerPosition.BOTTOM,

            //...other drawer properties
          })
        }
      >
        Open New Drawer
      </button>

      {/* Display currently open drawers */}
      {drawerState.items !== undefined && (
        <div style={{ backgroundColor: 'red' }}>
          {drawerState.items.map((drawer: any) => (
            <div key={drawer.id}>
              {/* <MainContainer /> */}
              <DrawerItem drawer={drawer} />
              <button onClick={() => handleCloseDrawer(drawer.id)}>Close Drawer</button>
            </div>
          ))}
        </div>
      )}

      {/* Button to close all drawers */}
      <button onClick={handleCloseAllDrawers}>Close All Drawers</button>
    </div>
  );
};

export default MyComponent;
