import React, { useEffect, useState } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { ToastType } from '../types';
import { useDispatch } from 'react-redux';

const ToastItem = ({ payload }: any) => {
  const { type, message, margin, duration = 3500, useCloseButton } = payload;
  const [mounted, setMounted] = useState(false);

  const dispatch = useDispatch();

  const toastMessage = () => {
    if (typeof message === 'string') {
      return message;
    } else if (message?.data?.error) {
      return message.data.error;
    } else if (message?.error_msg) {
      return message.error_msg;
    } else if (message?.details) {
      return message.details;
    } else if (typeof message === 'object') {
      return message.toString();
    }
    return 'Unknown Message';
  };

  const itemPosition = {
    bottom: margin?.bottom ? `${margin.bottom}px` : null,
    top: margin?.top ? `${margin.top}px` : null,
    left: margin?.left ? `${margin.left}px` : null,
    right: margin?.right ? `${margin.right}px` : null,
  };

  useEffect(() => {
    if (duration !== null) {
      const timeout = setTimeout(() => {
        // Call hide toast function here
        // e.g., toastModule.hide(props.data);
      }, duration);
      return () => clearTimeout(timeout);
    }
  }, [duration]);

  function closeToast() {
    setMounted(false);
    setTimeout(() => {
      dispatch({ type: 'DISMISS_TOAST', payload: payload.id });
    }, 250);
  }

  function getTransitionName() {
    if (mounted) {
      return 'fadeIn-center';
    }
    return 'fadeOut-center';
  }

  return (
    <TransitionGroup className="transition-group">
      <CSSTransition in={mounted} timeout={250} classNames={getTransitionName()}>
        <li className={`toast-item ${type}`}>
          <div className="container">
            {/* For simplicity, I'm using a placeholder icon instead of SVG */}
            <p className="toast-body">{message}</p>
            {useCloseButton && (
              <button
                onClick={() => {
                  closeToast();
                  // Call hide toast function here
                  // e.g., toastModule.hide(props.data);
                }}
              >
                닫기
              </button>
            )}
          </div>
        </li>
      </CSSTransition>
    </TransitionGroup>
  );
};

export default ToastItem;
