import { styled } from 'styled-components';

export const PaymentInfoContainer = styled.div`
  width: 100%;
  height: 100%;
  padding: 0 16px;
  margin-top: 16px;
`;

export const PaymentInfoUl = styled.ul`
  display: flex;
  gap: 6px;
  flex-wrap: wrap;
`;
export const PaymentInfoLi = styled.li`
  width: 32%;
  height: 42px;
  line-height: 42px;
  color: var(#333);
  text-align: center;
  font-family: Pretendard;
  font-size: 14px;
  font-weight: 500;
  border-radius: 6px;
  border: 0.5px solid #b9bdc5;
  flex-shrink: 0;
`;
