import { styled } from 'styled-components';

export const PaymentInfoContainer = styled.div`
  width: 100%;
  height: 100%;
  padding: 0 16px;
  margin-top: 16px;
`;

export const PaymentInfoUl = styled.ul`
  display: flex;
  gap: 6px;
  flex-wrap: wrap;
`;
export const PaymentInfoLi = styled.li`
  width: auto;
  min-width: 33%;
  height: 42px;
  line-height: 42px;
  color: var(--gray-2, #4d4d4d);
  text-align: center;
  font-family: Pretendard;
  font-size: 14px;
  font-weight: 400;
  letter-spacing: -0.7px;
  white-space: pre;
  border: 0.5px solid #b9bdc5;
  border-radius: 6px;
  flex-grow: 1;
  padding: 0 6px;
`;
