import { styled } from 'styled-components';

export const FishAdoptionContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  padding: 0 16px;
`;

export const FishAdoptionHeader = styled.div`
  width: 100%;
  height: 56px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  line-height: 56px;
  background-color: #fff;
  /* padding: 0 16px; */
  padding-top: 24px;
  margin-bottom: 20px;
`;

export const FishAdoptionPrice = styled.div`
  color: var(--Main-Dark, #333);
  text-align: center;
  font-family: Pretendard;
  font-size: 20px;
  font-style: normal;
  font-weight: 700;
  line-height: 22px;
`;

export const FishAdoptionBody = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: space-between;
  /* flex-wrap: wrap; */
`;
export const FishAdoptionQuantity = styled.div`
  background-color: #fff;
  padding: 0 16px;
  font-size: 16px;
  line-height: 48px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start;
`;

export const FishAdoptionQuantitySelectorArea = styled.div`
  width: 140px;
  height: 30px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-around;
  border-radius: 5px;
  border: 0.5px solid var(--main-gary, #b9bdc5);
`;

export const FishAdoptionQuantitySelectorButton = styled.div`
  width: 16px;
  height: 16px;
`;
export const FishAdoptionQuantitySelectorInput = styled.div``;

export const FishAdoptionButtonContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
`;

export const FishAdoptionButton = styled.div`
  width: 100%;
  height: 56px;
  background-color: #ff3131;
  display: flex;
  justify-content: center;
  align-items: center;
  color: #fff;
  font-size: 16px;
  font-weight: 500;
  line-height: 56px;
  cursor: pointer;
`;
