import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import MainContainer from '../../../MainContainer';
import './Drawer.css';
import { styled } from 'styled-components';

const StyleLi = styled.li`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  overflow: hidden;
  transition: all 0.3s ease-in-out;
  &.up {
    /* transform: translateY(0%); */
    position: fixed;
    bottom: 0;
  }
  &.down {
    transform: translateY(0);
  }
  &.left {
    transform: translateX(-100%);
  }
  &.right {
    transform: translateX(100%);
  }
  &.fade {
    opacity: 0;
  }
`;

const DrawerItem = ({ payload }: any) => {
  const [mounted, setMounted] = useState(false);
  const background = useRef(null);

  const styles = { backgroundColor: payload?.backgroundColor };
  const transitionName = getTransitionName();

  useEffect(() => {
    console.log('####');
  }, []);

  useEffect(() => {
    (async () => {
      await openDrawer();
    })();
  }, []);

  async function openDrawer() {
    await Promise.resolve();
    setMounted(true);
  }

  async function closeDrawer() {
    setMounted(false);
    await Promise.resolve();
    // TODO: Add the equivalent dismiss logic
  }

  function onMouseDown(e: any) {
    if (payload?.canCloseForModal && e.target === background.current && e.which === 1) {
      closeDrawer();
    }
  }

  function getTransitionName() {
    // TODO: Complete this logic based on your requirements
    return 'drawer-item';
  }

  return (
    <TransitionGroup className="transition-group">
      <CSSTransition in={mounted} timeout={250} classNames="up">
        <StyleLi className={`up`} ref={background} onMouseDown={onMouseDown} onTouchStart={onMouseDown}>
          <div className="container" style={styles} onClick={(e) => e.stopPropagation()}>
            {/* Dynamic component rendering... */}
            {/* <MainContainer /> */}
            <payload.component />
          </div>
        </StyleLi>
      </CSSTransition>
    </TransitionGroup>
  );

  function getClassName() {
    // TODO: This function will generate a class name based on the payload properties.
  }
};

DrawerItem.propTypes = {
  payload: PropTypes.object.isRequired,
};

export default DrawerItem;
