import { lazy, useEffect, useState } from 'react';
import * as m from './style/MainContainerStyle';

const MainContainer = () => {
  const [pay, setPay] = useState(false);

  const [userInfo, setUserInfo] = useState({
    name: '이승원',
    age: '29',
    job: '개발자',
    favorite: '코딩',
  });

  function changeUserInfo() {
    const { favorite, job, age } = userInfo;

    return `${favorite} ${job} ${age}}`;
  }

  useEffect(() => {
    console.log('###');
  }, []);

  return (
    <m.MainContainerWrap>
      <m.MainContainerArea>
        <m.MainContainerBg>
          <m.MainContainerTitle>
            <p>
              {changeUserInfo()}
              <br />
              <span>드로어 테스트 중입니다.</span>
            </p>
          </m.MainContainerTitle>
        </m.MainContainerBg>
      </m.MainContainerArea>
    </m.MainContainerWrap>
  );
};
export default MainContainer;
