import { styled } from 'styled-components';

export const PaymentInfoContainer = styled.div`
  width: 100%;
  height: 100%;
  margin-top: 16px;
`;

export const PaymentInfoTotalPrice = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 16px;
`;

export const PaymentInfoDiscountPrice = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 16px;
`;

export const PaymentInfoDeliveryPrice = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 16px;
`;

export const PaymentInfoTotalPriceBox = styled.div`
  border-top: 1px solid #b9bdc5;
  padding-top: 24px;
  display: flex;
  justify-content: space-between;
`;

export const TotalPriceTitle = styled.div`
  color: #333;
  font-family: Pretendard;
  font-size: 18px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
`;

export const TotalPrice = styled.div`
  color: #004bca;
  font-family: Pretendard;
  font-size: 18px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
`;

export const PaymentInfoPriceTitle = styled.div`
  color: #b9bdc5;
  font-family: Pretendard;
  font-size: 14px;
  font-weight: 400;
`;
export const PaymentInfoPrice = styled.div`
  color: #4d4d4d;
  text-align: right;
  font-family: Pretendard;
  font-size: 14px;
  font-weight: 400;
`;
