import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { presentDrawer, dismissDrawer, dismissAll } from './components/drawer/DrawerAction'; // Ensure path is correct

function MyComponent() {
  // Use the drawer state from Redux
  const drawerState = useSelector((state) => state.drawer);

  // Dispatch function to dispatch actions
  const dispatch = useDispatch();

  // Open a new drawer item
  const handleOpenDrawer = (drawerPayload) => {
    dispatch(presentDrawer(drawerPayload));
  };

  // Close a specific drawer item
  const handleCloseDrawer = (drawerId) => {
    dispatch(dismissDrawer(drawerId));
  };

  // Close all drawers
  const handleCloseAllDrawers = () => {
    dispatch(dismissAll());
  };

  return (
    <div>
      {/* Display a button to open a new drawer */}
      <button
        onClick={() =>
          handleOpenDrawer({
            id: 'sampleId',
            component: 'SampleComponent',
            //...other drawer properties
          })
        }
        s
      >
        Open New Drawer
      </button>

      {/* Display currently open drawers */}
      {drawerState.items.map((item) => (
        <div key={item.id}>
          {/* Render drawer item */}
          <button onClick={() => handleCloseDrawer(item.id)}>Close This Drawer</button>
        </div>
      ))}

      {/* Button to close all drawers */}
      <button onClick={handleCloseAllDrawers}>Close All Drawers</button>
    </div>
  );
}

export default MyComponent;
